document.getElementById("convertir").addEventListener("click",()=>{const e=document.getElementById("expresion").value.trim(),n=document.getElementById("comparar").value.trim(),r=document.getElementById("resultado");r.innerHTML="",!e&&(r.innerText="Por favor, ingresa una expresión en el primer campo."),!n&&(r.innerText="Por favor, ingresa una expresión regular en el segundo campo.");try{const t=new RegExp(n,"g"),o=e.match(t);r.innerHTML=o?`<p>Se encontraron ${o.length} coincidencia(s): ${o.join(", ")}</p>`:"<p>No se encontraron coincidencias.</p>"}catch(e){r.innerText="Error en la expresión regular ingresada."}});